<?php/**** @package    Basic MVC framework* @author     Jeremie Litzler* @copyright  Copyright (c) 2015* @license* @link* @since* @filesource*/// ------------------------------------------------------------------------/**** Document Dao Class** @package     Application/PMTool* @subpackage  Models/Dao* @category    Document* @author      FWM DEV Team* @link*/namespace Applications\PMTool\Models\Dao;if ( ! defined('__EXECUTION_ACCESS_RESTRICTION__')) exit('No direct script access allowed');class Document extends \Library\Entity implements \Library\Interfaces\IDocument, \JsonSerializable {  public     $document_id,    $document_content_type,    $document_category,    $document_value,    $document_size,    $document_title;  // members required for document interface  private    $webPath,    $absolutePath;  const     DOCUMENT_ID_ERR = 0,    DOCUMENT_CONTENT_TYPE_ERR = 1,    DOCUMENT_CATEGORY_ERR = 2,    DOCUMENT_VALUE_ERR = 3,    DOCUMENT_SIZE_ERR = 4,    DOCUMENT_TITLE_ERR = 5;  // SETTERS //  public function setDocument_id($document_id) {      $this->document_id = $document_id;  }  public function setDocument_content_type($document_content_type) {      $this->document_content_type = $document_content_type;  }  public function setDocument_category($document_category) {      $this->document_category = $document_category;  }  public function setDocument_value($document_value) {      $this->document_value = $document_value;  }  public function setDocument_size($document_size) {      $this->document_size = $document_size;  }  public function setDocument_title($document_title) {    $this->document_title = $document_title;  }  // GETTERS //  public function document_id() {    return $this->document_id;  }  public function document_content_type() {    return $this->document_content_type;  }  public function document_category() {    return $this->document_category;  }  public function document_value() {    return $this->document_value;  }  public function document_size() {    return $this->document_size;  }  public function document_title() {    return $this->document_title;  }  // Document interface //  public function setWebPath($webPath){    $this->webPath = $webPath;  }  public function setAbsolutePath($absolutePath) {    $this->absolutePath = $absolutePath;  }  public function setFilename($filename) {    $this->document_value = $filename;  }  public function setContentType($contentType) {    $this->document_content_type = $contentType;  }  public function setContentSize($contentSize) {    $this->document_size = $contentSize;  }  public function WebPath(){    return $this->webPath;  }  public function AbsolutePath() {    return $this->absolutePath;  }  public function Filename() {    return $this->document_value;  }  public function ContentSize() {    return $this->document_size;  }  public function ContentType() {    return $this->document_content_type;  }  public function ValidExtensions() {    return array("jpeg","jpg","gif","png","pdf");  }  public function jsonSerialize() {    return array(      "document_id"=>$this->document_id,      "document_content_type"=>$this->document_content_type,      "document_category"=>$this->document_category,      "document_value"=>$this->document_value,      "document_size"=>$this->document_size,      "document_title"=>$this->document_title,      "title"=>$this->document_title,      "webPath"=>$this->webPath,      "absolutePath"=>$this->absolutePath    );  }}